sp_widget.client_script:
api.controller=function(spUtil) {
  /* widget controller */
  var c = this;
	c.filter = '';
	c.submitting = false;

	c.submit = function() {

		if(c.data.newIncident.issue === '' || c.data.newIncident.urgency === "") {
			spUtil.addErrorMessage("Issue and urgency are required.")
			return;
		}

		c.data.action = "newTicket";
		c.submitting = true;

		c.server.update().then(function() {
			c.submitting = false;
			spUtil.addInfoMessage(c.data.message);
		});
	}
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.fields = 'number, short_description, sys_created_on'
	data.items = getRecentIncidents();
	data.newIncident = {
		issue: "",
		urgency: "",
	};
	var portal = $sp.getPortalRecord();
	data.portalName = portal.getDisplayValue('title');
	data.parameter = $sp.getParameter('test');

	if(input && input.action === 'newTicket'){
		createIncident(input);
		data.message = "An incident has been created";
	}

	function createIncident(inc) {
		var incidentGR = new GlideRecord('incident');
		incidentGR.newRecord();
		incidentGR.setValue('caller_id', gs.getUserID());
		incidentGR.setValue('short_description', inc.issue);
		incidentGR.setValue('urgency', inc.urgency);
		incidentGR.insert();
	}

	function getRecentIncidents(){
		var items = [];
		var incidentGR = new GlideRecord('incident');

		incidentGR.setLimit(5);
		incidentGR.orderByDesc('sys_created_on');
		incidentGR.query();

		while(incidentGR.next()) {
			var obj = {};
			$sp.getRecordElements(obj, incidentGR, data.fields)
			items.push(obj);
		}

		console.log(items);
		return items
	}

})();
